mapper.select:
select
  id, create_id, create_time, update_id, update_time, question_content, choice_A, choice_B, choice_C, choice_D, question_answer, question_explain, question_score, question_level, question_subject, question_type
from boot_shiro_vue_stu.question_choic


        and id = #{id}


        and create_id = #{createId}


        and create_time = #{createTime}


        and update_id = #{updateId}


        and update_time = #{updateTime}


        and question_content = #{questionContent}


        and choice_A = #{choiceA}


        and choice_B = #{choiceB}


        and choice_C = #{choiceC}


        and choice_D = #{choiceD}


        and question_answer = #{questionAnswer}


        and question_explain = #{questionExplain}


        and question_score = #{questionScore}


        and question_level = #{questionLevel}


        and question_subject = #{questionSubject}


        and question_type = #{questionType}
