mapper.select:
select
  id, exam_id, join_id, join_date, join_time_cost, join_score, join_result_level, join_answer_ids
from boot_shiro_vue_stu.question_exam_record


        and id = #{id}


        and exam_id = #{examId}


        and join_id = #{joinId}


        and join_date = #{joinDate}


        and join_time_cost = #{joinTimeCost}


        and join_score = #{joinScore}


        and join_result_level = #{joinResultLevel}


        and join_answer_ids = #{joinAnswerIds}
